<?php\nuse Ezyang\PhpUnixUser\User;\n\n$username = 'myuser';\n$password = 'mypassword';\n\n$user = new User($username);\nif ($user->exists()) {\n    if ($user->authenticate($password)) {\n        // Authentication successful, retrieve group membership\n        $groups = $user->getGroups();\n        print_r($groups);\n    } else {\n        echo "Authentication failed";\n    }\n} else {\n    echo "User does not exist";\n}\n?>