<?php\n$username = 'myuser';\n$password = 'mypassword';\n\nif (posix_getpwnam($username)) {\n    // User exists, try to authenticate\n    if (posix_getpwuid(posix_getuid())['pw_passwd'] == crypt($password, posix_getpwuid(posix_getuid())['pw_passwd'])) {\n        // Authentication successful, retrieve group membership\n        $groups = posix_getgroups();\n        print_r($groups);\n    } else {\n        echo "Authentication failed";\n    }\n} else {\n    echo "User does not exist";\n}\n?>