// ModelRegistry.php\nclass ModelRegistry {\n    private static $instances = array();\n\n    public static function getInstance($modelName) {\n        if (!isset(self::$instances[$modelName])) {\n            self::$instances[$modelName] = new $modelName();\n        }\n        return self::$instances[$modelName];\n    }\n}\n\n// Relation_Model.php\nclass Relation_Model extends CI_Model {\n    public function getRelation($userID) {\n        $userModel = ModelRegistry::getInstance('User_Model');\n        // Use the instance from the registry\n        $user = $userModel->getUser($userID);\n        // ...\n    }\n}\n\n// Reminder_Model.php\nclass Reminder_Model extends CI_Model {\n    public function someMethod() {\n        $userModel = ModelRegistry::getInstance('User_Model');\n        // Use the instance from the registry\n        // ...\n    }\n}