// Symfony controller\nuse Symfony\Component\HttpFoundation\Request;\nuse Symfony\Component\HttpFoundation\Response;\n\nclass AlternativeContentController extends Controller\n{\n    public function indexAction(Request $request)\n    {\n        // Check if request is made by a non-Flash user or search engine bot\n        if (!$request->isXmlHttpRequest() || $request->headers->get('User-Agent') == 'bot') {\n            // Serve alternative text content\n            $content = $this->getDoctrine()->getRepository('AppBundle:Content')->find($request->get('specific_page'));\n            return new Response($content->getText(), 200, array('Content-Type' => 'text/html'));\n        } else {\n            // Let the Flash application handle the request\n            return new Response('', 200, array('Content-Type' => 'text/plain'));\n        }\n    }\n}