$sleep1 = rand(2, 18);\n$sleep2 = rand(2, 18);\n$sleep3 = rand(2, 18);\n$sleep4 = rand(2, 18);\n\n$totalSleep = $sleep1 + $sleep2 + $sleep3 + $sleep4;\n\nif ($totalSleep < 60) {\n    $diff = 60 - $totalSleep;\n    // increase the value of one or more variables to make up the difference\n    // for simplicity, let's increase the first variable\n    $sleep1 += $diff;\n}\n\n// now the total sleep time is at least 60 seconds