class ConfigDB {\n  private $pdo;\n\n  public function __construct($dbPath) {\n    $this->pdo = new PDO("sqlite:$dbPath");\n  }\n\n  public function saveConfig($name, $config) {\n    $stmt = $this->pdo->prepare("INSERT INTO configs (name, config) VALUES (:name, :config)");\n    $stmt->bindParam(":name", $name);\n    $stmt->bindParam(":config", $config);\n    $stmt->execute();\n  }\n\n  public function getConfig($id) {\n    $stmt = $this->pdo->prepare("SELECT config FROM configs WHERE id = :id");\n    $stmt->bindParam(":id", $id);\n    $stmt->execute();\n    return $stmt->fetchColumn();\n  }\n}