function log_item_changes($post_id) {\n    global $wpdb;\n    // Get the post type\n    $post_type = get_post_type($post_id);\n    // Check if it's an item\n    if ($post_type === 'item') {\n        // Log the changes\n        $wpdb->insert('change_log', array(\n            'user_id' => get_current_user_id(),\n            'action' => 'UPDATE',\n            'table_name' => 'Item',\n            'row_id' => $post_id,\n            'changes' => json_encode($_POST),\n        ));\n    }\n}\nadd_action('save_post', 'log_item_changes');\n\nfunction log_item_deletion($post_id) {\n    global $wpdb;\n    // Get the post type\n    $post_type = get_post_type($post_id);\n    // Check if it's an item\n    if ($post_type === 'item') {\n        // Log the deletion\n        $wpdb->insert('change_log', array(\n            'user_id' => get_current_user_id(),\n            'action' => 'DELETE',\n            'table_name' => 'Item',\n            'row_id' => $post_id,\n            'changes' => json_encode(get_post($post_id)),\n        ));\n    }\n}\nadd_action('delete_post', 'log_item_deletion');