<?php\n// Configuration\n$dbHost = 'your_host';\n$dbUsername = 'your_username';\n$dbPassword = 'your_password';\n$dbName = 'your_database';\n\n// Connect to database\n$conn = new mysqli($dbHost, $dbUsername, $dbPassword, $dbName);\n\n// Check connection\nif ($conn->connect_error) {\n    die("Connection failed: " . $conn->connect_error);\n}\n\n// Read email data from STDIN\n$emailData = file_get_contents('php://stdin');\n\n// Parse email data (e.g., extract relevant information)\n$subject = '';\n$body = '';\nforeach (explode("\n", $emailData) as $line) {\n    if (preg_match('/^Subject: (.*)$/', $line, $matches)) {\n        $subject = $matches[1];\n    } elseif (preg_match('/^\s*(.*)$/', $line, $matches)) {\n        $body .= $matches[1] . "\n";\n    }\n}\n\n// Insert data into database\n$query = "INSERT INTO your_table (subject, body) VALUES (?, ?)";\n$stmt = $conn->prepare($query);\n$stmt->bind_param("ss", $subject, $body);\n$stmt->execute();\n\n// Close database connection\n$conn->close();\n?>