// assuming you have a session handler class\n$session = new SessionHandler();\n\n// create a token that corresponds to the user's session\n$token = bin2hex(random_bytes(32)); // generate a random token\n$session->set('token', $token);\n\n// prepare the data to be shared with the client-side\n$sharedData = array(\n    'username' => $session->get('username'),\n    'preference_id' => $session->get('preference_id')\n);\n\n// encode the shared data using JWT\n$jwt = JWT::encode($sharedData, $token, 'HS256');