// assuming you have a session handler class\n$session = new SessionHandler();\n\n// retrieve the token and shared data\n$token = $session->get('token');\n$sharedData = JWT::decode($token, $session->get('token'), array('HS256'));\n\n// return the token and shared data as JSON\nheader('Content-Type: application/json');\necho json_encode(array(\n    'token' => $token,\n    'sharedData' => $sharedData\n));