$mysqli = new mysqli("localhost", "username", "password", "database");\n\n// Get the current timestamp\n$current_timestamp = time();\n\n// Get the last updated timestamp from the database\n$result = $mysqli->query("SELECT last_updated FROM mytable WHERE id = 1");\n$row = $result->fetch_assoc();\n$last_updated_timestamp = strtotime($row['last_updated']);\n\n// Calculate the time difference in minutes\n$time_diff_minutes = ($current_timestamp - $last_updated_timestamp) / 60;\n\nif ($time_diff_minutes >= 30) {\n    echo "Updated more than 30 minutes ago";\n} else {\n    echo "Updated $time_diff_minutes minutes ago. Time left until 30 minutes: " . (30 - $time_diff_minutes) . " minutes";\n}