class Model_Company extends LP_Model\n{\n    private $gateway;\n\n    public function __construct(GatewayInterface $gateway)\n    {\n        $this->gateway = $gateway;\n    }\n\n    public function get($param)\n    {\n        if ($param instanceof Zend_Db_Table_Row_Abstract) {\n            $row = $param;\n        } elseif (is_numeric($param)) {\n            $row = $this->gateway->find($param)->current();\n        }\n\n        return new self($row);\n    }\n\n    public function getCollection($param = null)\n    {\n        if ($param instanceof Zend_Db_Table_Rowset_Abstract) {\n            $rowset = $param;\n        } elseif (!$param) {\n            $rowset = $this->gateway->fetchAll();\n        }\n\n        $array = [];\n\n        foreach ($rowset as $row) {\n            $array[] = new self($row);\n        }\n\n        return $array;\n    }\n}