$refererUrl = $_SERVER['HTTP_REFERER'];\n$refererTitle = '';\n\nif ($refererUrl) {\n    $refererHtml = file_get_contents($refererUrl);\n    $dom = new DOMDocument();\n    @$dom->loadHTML($refererHtml);\n    $refererTitle = $dom->getElementsByTagName('title')->item(0)->nodeValue;\n}\n\necho 'thanks for coming <a href="' . $refererUrl . '">here\'s a link back to ' . $refererTitle . '</a>';