// ...\n\n$sql = "SELECT DATE(order_placed_date) as order_date, SUM(order_total) as total_amount FROM orders GROUP BY DATE(order_placed_date) ORDER BY order_date";\n$result = mysql_query($sql);\n\n$dateData = array();\n$TotalAmountData = array();\n\nwhile($row = mysql_fetch_array($result)) {\n  $dateData[] = $row['order_date'];\n  $TotalAmountData[] = $row['total_amount'];\n}\n\n// ...\n\n$x_axis = new x_axis();\n$x_axis->set_range(min($dateData), max($dateData));\n$x_axis->set_steps(86400); // 1 day\n$x_axis->set_labels($dateData);\n\n// ...