$isJpg = strpos($file, '.jpg', 1) !== false;\n$isGif = strpos($file, '.gif', 1) !== false;\n$isPng = strpos($file, '.png', 1) !== false;\n\n$thumbPath = "$thumbsdir/$file";\n\nif (!file_exists($thumbPath) && ($isJpg || $isGif || $isPng)) {\n    createThumb("$gallerydir/$file", $thumbPath, $thumbsize);\n    fwrite($log, date("Y-m-d") . " @ " . date("H:i:s") . "  CREATED: $thumbPath\n");\n}