$filesQuery = "SELECT FILENAME FROM FILES WHERE ARTICLE_NO = ?";\n\nif ($getFiles = $con->prepare($filesQuery)) {\n    $getFiles->bind_param("s", $pk);\n    $getFiles->execute();\n    $getFiles->bind_result($FILENAME);\n    $files = array();\n\n    while ($getFiles->fetch()) {\n        $files[] = array(\n            'FILENAME' => $FILENAME,\n        );\n    }\n}\n\nvar_dump($files); // This will show you the structure of the $files array\n\nforeach ($files as $file) {\n    $filesList .= '<p><a href="./files/'. $file['FILENAME'] .'">'. $file['FILENAME'] .'</a>' . "\n";\n}\n\necho $filesList;