<?php\n\n// Connect to the IMAP server\n$mbox = imap_open("{imap.gmail.com:993/imap/ssl}INBOX", "your_username", "your_password");\n\n// Send the IDLE command\nimap_socket($mbox, "IDLE");\n\n// Wait for notifications\nwhile (true) {\n    $line = imap_socket($mbox, "");\n    if ($line !== false) {\n        // Process the notification\n        echo "Received: $line\n";\n    }\n}\n\n// Close the IMAP connection\nimap_close($mbox);\n\n?>