$string = 'product[0][1][0]';\n$array = [\n    'product' => [\n        [\n            [\n                'value1',\n                'value2',\n            ],\n            [\n                'value3',\n                'value4',\n            ],\n        ],\n    ],\n];\n\n$path = explode('[', $string);\n$path = array_filter($path);\n$path = array_map(function ($item) {\n    return rtrim($item, ']');\n}, $path);\n\n$value = $array;\nforeach ($path as $key) {\n    $value = $value[$key];\n}\n\necho $value; // Outputs: value3