function bcCeil($number) {\n    return bcadd($number, '0.9999999999999999999999999999999999999999', 0);\n}\n\nfunction bcFloor($number) {\n    return bcsub($number, '0.0000000000000000000000000000000000000001', 0);\n}\n\nfunction bcRound($number, $precision = 0) {\n    $multiplier = bcpow('10', $precision);\n    return bcdiv(bcadd(bcmul($number, $multiplier), '0.5'), $multiplier, $precision);\n}\n\necho bcCeil('31497230840470473074370324734723042.6'); // prints 31497230840470473074370324734723043