$args = array(\n    'post_type' => 'page',\n    'post_parent' => $parent_page_id, // optional, to query child pages of a specific page\n);\n\n$query = new WP_Query($args);\n\nif ($query->have_posts()) {\n    while ($query->have_posts()) {\n        $query->the_post();\n        // use THE LOOP function calls here\n        the_permalink();\n        the_title();\n    }\n}\n\nwp_reset_postdata();