$descriptorspec = array(\n    0 => array("pipe", "r"),  // stdin\n    1 => array("pipe", "w"),  // stdout\n    2 => array("pipe", "w")   // stderr\n);\n\n$process = proc_open('RUNMYSCRIPT.sh', $descriptorspec, $pipes);\n\nsleep(10);\n\n// Send a termination key to the script\nfwrite($pipes[0], "q");\nfflush($pipes[0]);\n\n// Close the pipes\nfclose($pipes[0]);\nfclose($pipes[1]);\nfclose($pipes[2]);\n\n// Get the return code of the process\n$return_code = proc_close($process);\n\n// You can check the return code to see if the process terminated correctly\nif ($return_code == 0) {\n    print "Process terminated correctly\n";\n} else {\n    print "Process did not terminate correctly\n";\n}