$query = new Zend_Search_Lucene_Search_Query_Boolean();\nforeach ($words as $word) {\n    $term1 = new Zend_Search_Lucene_Index_Term($word . '*', $fieldname1);\n    $term2 = new Zend_Search_Lucene_Index_Term($word . '*', $fieldname2);\n    $wildcard1 = new Zend_Search_Lucene_Search_Query_Wildcard($term1);\n    $wildcard2 = new Zend_Search_Lucene_Search_Query_Wildcard($term2);\n    $multiq = new Zend_Search_Lucene_Search_Query_Boolean();\n    $multiq->addSubquery($wildcard1, true);\n    $multiq->addSubquery($wildcard2, true);\n    $query->addSubquery($multiq, true);\n}\n$hits = $index->find($query);