// At the beginning of your script\nsession_start();\n\n// When the user logs in\nif ($passhash_md5 == md5($_REQUEST['password'])) {\n    $_SESSION['logged_in'] = true;\n    $_SESSION['email'] = $_REQUEST['email'];\n    $_SESSION['passhash_md5'] = md5($_REQUEST['password']);\n}\n\n// To make the session permanent\nsession_set_cookie_params(604800); // Set the session cookie's lifetime to 1 week\n\n// When the user requests a page\nif (isset($_SESSION['logged_in']) && $_SESSION['logged_in'] == true) {\n    // The user is logged in, you can access their data from the $_SESSION array\n    echo "Welcome, " . $_SESSION['email'];\n} else {\n    // The user is not logged in, redirect them to the login page\n    header("Location: login.php");\n    exit;\n}