$conn = mysqli_connect("localhost", "username", "password", "database");\n\n// Make sure to escape the file path and name\n$csvFile = mysqli_real_escape_string($conn, "/path/to/your/file.csv");\n\n$query = "LOAD DATA INFILE '$csvFile'\n          INTO TABLE your_table\n          FIELDS TERMINATED BY ','\n          ENCLOSED BY '\"'\n          LINES TERMINATED BY '\n'\n          IGNORE 1 ROWS"; // Ignore the header row\n\nmysqli_query($conn, $query);\n\nmysqli_close($conn);