function verify_Username_and_Pass($un, $pwd) {\n    $query = "SELECT *\n            FROM user_table\n            WHERE login_username = ? AND login_password = ?";\n\n    if($stmt = $this->conn->prepare($query)) {\n        $stmt->bind_param('ss', $un, $pwd);\n        $stmt->execute();\n        $stmt->store_result(); // Store the result set\n\n        if($stmt->num_rows > 0) { // Check if any rows were returned\n            $stmt->close();\n            return true;\n        }\n    }\n    return false;\n}