class Room extends AppModel {\n    public function isRoomAvailable($roomId, $startDate, $endDate) {\n        $conditions = array(\n            'Room.id' => $roomId,\n            'NOT' => array(\n                'RoomBooking.start_date BETWEEN ? AND ?' => array($startDate, $endDate),\n                'RoomBooking.end_date BETWEEN ? AND ?' => array($startDate, $endDate)\n            )\n        );\n        $result = $this->find('first', array('conditions' => $conditions));\n        return !empty($result);\n    }\n}