function findBetween($ar, $n) {\n    $low = 0;\n    $high = count($ar) - 1;\n\n    while ($low <= $high) {\n        $mid = floor(($low + $high) / 2);\n        if ($ar[$mid] == $n) {\n            return array($ar[$mid], $ar[$mid]);\n        } elseif ($ar[$mid] < $n) {\n            $low = $mid + 1;\n        } else {\n            $high = $mid - 1;\n        }\n    }\n\n    return array($ar[$low - 1], $ar[$low]);\n}\n\n$ar = array(5, 10, 15, 20);\n$n = 8;\n\n$result = findBetween($ar, $n);\n\nprint_r($result); // Output: Array ( [0] => 5 [1] => 10 )