// Use prepared statements to prevent SQL injection\n$stmt = $mysqli->prepare("INSERT INTO `reunie` (`naam`, `van`, `nooiensvan`, `selfoon`, `email`, `bywoon`, `metgesel_naam`, `spesifieke_onderwysers`, `spesifieke_musiek`, `bydrae`, `voorstelle`) VALUES (?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?)");\n\n$stmt->bind_param("sssssssssss", $naam, $van, $nooiensvan, $selfoon, $email, $bywoon, $metgesel, $spesifiekeOnderwysers, $musiek, $bydrae, $voorstelle);\n\n$stmt->execute();\n\n// Get the inserted ID securely using the insert_id property\n$inserted_id = $mysqli->insert_id;\n\n// Use prepared statements for the second query as well\n$stmt = $mysqli->prepare("INSERT INTO `another_table` (`id`) VALUES (?)");\n$stmt->bind_param("i", $inserted_id);\n$stmt->execute();