$countAuctionsQuery = "SELECT COUNT(*) FROM AUCTIONS WHERE username = ? AND SUBCAT = ?";\n\n// Define the categories array\n$categories = ['fake', 'ok', 'sold', 'another_category'];\n\n// Initialize an empty array to store the count of each category\n$categoryCounts = [];\n\n// Loop through each category and execute the query\nforeach ($categories as $category) {\n    if ($getRecords = $con->prepare($countAuctionsQuery)) {\n        $getRecords->bind_param("ss", $username, $category);\n        $getRecords->execute();\n        $getRecords->bind_result($numRecords);\n        $getRecords->fetch();\n        $categoryCounts[$category] = $numRecords;\n    }\n}\n\n// Print out the results\nforeach ($categoryCounts as $category => $count) {\n    echo ucfirst($category) . " items: $count\n";\n}