function validate_and_sanitize_url($url) {\n    // Check if URL starts with http or https\n    if (!preg_match('#^https?://#', $url)) {\n        // If not, add http://\n        $url = 'http://' . $url;\n    }\n    return $url;\n}\n\n// Example usage:\n$url = 'theirsite.com';\n$validated_url = validate_and_sanitize_url($url);\necho $validated_url; // Output: http://theirsite.com\n\n// Usage in your redirect scenario:\n$link = validate_and_sanitize_url($_GET['link']);\nheader("Location: $link");