if ($db = new PDO('sqlite:./db/blog.sqlite3')) {\n    $stmt = $db->prepare("SELECT * FROM news ORDER BY date DESC");\n    if ($stmt->execute()) {\n        $last_year = null;\n        $last_month = null;\n        while ($row = $stmt->fetch(PDO::FETCH_NUM)) {\n            $current_year = date("Y", $row[1]);\n            $current_month = date("F", $row[1]); // Use "F" for full month name\n            if ($current_year !== $last_year) {\n                echo "<h1>$current_year</h1>";\n                $last_year = $current_year;\n                $last_month = null;\n            }\n            if ($current_month !== $last_month) {\n                echo "<h2>$current_month</h2>";\n                $last_month = $current_month;\n            }\n            echo "<p><a href='view_post.php?post_id=$row[1]'>$row[0] - " . date("Y-m-d, H:i:s", $row[1]) . "</a></p>";\n        }\n    }\n} else {\n    die($sqliteerror);\n}