require __DIR__ . '/vendor/autoload.php';\n\nuse phpseclib3\Net\SFTP;\n\n$sourceServer = 'sftp://username:password@source-server.com';\n$destinationServer = 'sftp://username:password@destination-server.com';\n\n$sourceSftp = new SFTP($sourceServer);\n$destinationSftp = new SFTP($destinationServer);\n\n// Connect to the source server\nif (!$sourceSftp->login()) {\n    throw new Exception('Failed to connect to source server');\n}\n\n// Connect to the destination server\nif (!$destinationSftp->login()) {\n    throw new Exception('Failed to connect to destination server');\n}\n\n// Transfer the file\n$sourceFile = '/path/to/source/file.xml';\n$destinationFile = '/path/to/destination/file.xml';\n$sourceSftp->put($destinationFile, $sourceFile, SFTP::SOURCE_LOCAL_FILE);\n\n// Close the SFTP connections\n$sourceSftp->disconnect();\n$destinationSftp->disconnect();