// Good practice: Use functions for readability and maintainability\nfunction calculateTotal(array $items): float {\n    $total = 0;\n    foreach ($items as $item) {\n        $total += $item['price'] * $item['quantity'];\n    }\n    return $total;\n}\n\n// Bad practice: Avoid flat, repetitive code\n$items = [...]; // assume an array of items\n$total = 0;\nforeach ($items as $item) {\n    $total += $item['price'] * $item['quantity'];\n}