function is_multidimensional(array $array, $level = 0): bool {\n    if ($level > 0) return true; // Already found a nested array\n    foreach ($array as $value) {\n        if (is_array($value)) {\n            return is_multidimensional($value, $level + 1);\n        }\n    }\n    return false;\n}\n\n// Example usage:\n$array1 = [1, 2, 3];\n$array2 = [1, [2], 3];\n\nvar_dump(is_multidimensional($array1)); // bool(false)\nvar_dump(is_multidimensional($array2)); // bool(true)