<?php\n// Configuration\n$stampImage = 'stamp.png'; // Path to your stamp image\n$stampWidth = 100; // Width of the stamp\n$stampHeight = 50; // Height of the stamp\n$margin = 10; // Margin around the stamp\n\n// Load the uploaded image\n$uploadedImage = $_FILES['image']['tmp_name'];\n$image = imagecreatefromjpeg($uploadedImage);\n\n// Get the image dimensions\n$width = imagesx($image);\n$height = imagesy($image);\n\n// Create a new image with the stamp\n$newImage = imagecreatetruecolor($width, $height);\nimagecopy($newImage, $image, 0, 0, 0, 0, $width, $height);\n\n// Load the stamp image\n$stamp = imagecreatefrompng($stampImage);\n\n// Calculate the position of the stamp\n$stampX = $margin;\n$stampY = $margin;\n\n// Copy the stamp onto the new image\nimagecopy($newImage, $stamp, $stampX, $stampY, 0, 0, $stampWidth, $stampHeight);\n\n// Save the new image\nimagejpeg($newImage, 'output.jpg', 90);\n\n// Clean up\nimagedestroy($image);\nimagedestroy($newImage);\nimagedestroy($stamp);\n?>