<?php\n// Configuration\n$dbHost = 'your_host';\n$dbUsername = 'your_username';\n$dbPassword = 'your_password';\n$dbName = 'your_database';\n\n// Create a connection\n$conn = new mysqli($dbHost, $dbUsername, $dbPassword, $dbName);\n\n// Check connection\nif ($conn->connect_error) {\n    die("Connection failed: " . $conn->connect_error);\n}\n\n// Function to check if SheetNumber exists\nfunction checkSheetNumber($sheetNumber) {\n    $query = "SELECT * FROM your_table WHERE SheetNumber = ?";\n    $stmt = $conn->prepare($query);\n    $stmt->bind_param("s", $sheetNumber);\n    $stmt->execute();\n    $result = $stmt->get_result();\n    if ($result->num_rows > 0) {\n        return true; // SheetNumber exists\n    } else {\n        return false; // SheetNumber does not exist\n    }\n}\n\n// Example usage\n$sheetNumber = 'example_sheet_number';\nif (checkSheetNumber($sheetNumber)) {\n    echo "SheetNumber exists!";\n} else {\n    echo "SheetNumber does not exist!";\n}\n\n// Close the connection\n$conn->close();\n?>