$videoFile = 'path/to/video.mp4';\n$segmentDuration = 10; // seconds\n\nheader('Content-Type: application/x-mpegURL');\nheader('Content-Disposition: inline; filename="stream.m3u8"');\n\n$fp = fopen($videoFile, 'rb');\n$segmentCount = 0;\nwhile (!feof($fp)) {\n    $segment = fread($fp, $segmentDuration * 1024 * 1024); // 10MB\n    $segmentCount++;\n    echo "#EXTINF:$segmentDuration\n";\n    echo "segment$segmentCount.ts\n";\n}\nfclose($fp);