class CustomAutoloader implements Zend_Loader_Autoloader_Interface\n{\n    public function autoload($class)\n    {\n        $module = substr($class, 0, strpos($class, '_'));\n        $controller = substr($class, strpos($class, '_') + 1);\n        $file = APPLICATION_PATH . '/modules/' . $module . '/controllers/' . $controller . '.php';\n        \n        if (file_exists($file)) {\n            include $file;\n            return true;\n        }\n        \n        return false;\n    }\n}\n\n$autoloader = new CustomAutoloader();\nZend_Loader_Autoloader::getInstance()->pushAutoloader($autoloader);\n\n$modules = new Modules();\n$activeModules = $modules->fetchActive();\nforeach($activeModules as $mod) {\n    $loadedModules[$mod->name] = '..application/modules/' . $mod->name . '/controllers';\n    if($mod->default) {\n        $defaultModule = $mod->name;\n    }\n}\n\n$frontController->setControllerDirectory($loadedModules);\n$frontController->setDefaultModule($defaultModule);