class UserData {\n    public $name;\n\n    public function __construct() {\n        $backtrace = debug_backtrace();\n        $variableName = $backtrace[0]['args'][0];\n        $id = preg_replace('/[^0-9]/', '', $variableName);\n        // Query the database using the extracted ID\n        // For demonstration purposes, assume we have a function `getUserInfo($id)`\n        $userInfo = getUserInfo($id);\n        $this->name = $userInfo['name'];\n    }\n}\n\nfunction getUserInfo($id) {\n    // Simulate a database query\n    $users = [\n        24 => ['name' => 'Bob'],\n        // ...\n    ];\n    return $users[$id] ?? ['name' => 'Unknown'];\n}\n\n$user24 = new UserData();\necho 'Welcome, ' . $user24->name . '!'; // Output: Welcome, Bob!