class UserModel extends Zend_Db_Table_Abstract\n{\n    protected $_name = 'users';\n    protected $_dependentTables = array('UserPassword', 'UserMetadata');\n\n    public function getMetadata($userId)\n    {\n        $metadataTable = new UserMetadata();\n        $metadataRow = $metadataTable->find($userId)->current();\n        return $metadataRow;\n    }\n\n    public function getPassword($userId)\n    {\n        $passwordTable = new UserPassword();\n        $passwordRow = $passwordTable->find($userId)->current();\n        return $passwordRow;\n    }\n}\n\nclass UserPassword extends Zend_Db_Table_Abstract\n{\n    protected $_name = 'user_password';\n    protected $_referenceMap = array(\n        'User' => array(\n            'columns' => array('user_id'),\n            'refTableClass' => 'UserModel',\n            'refColumns' => array('id')\n        )\n    );\n}\n\nclass UserMetadata extends Zend_Db_Table_Abstract\n{\n    protected $_name = 'user_metadata';\n    protected $_referenceMap = array(\n        'User' => array(\n            'columns' => array('user_id'),\n            'refTableClass' => 'UserModel',\n            'refColumns' => array('id')\n        )\n    );\n}