$param = array('customer_id' => 1, 'qty' => '2');\n$stmt = $mysqli->stmt_init();\n\n$types = '';\n$where = '';\n$bindValues = [];\n\nforeach ($param as $key => $val) {\n    $types .= 'i';\n    $where .= "$key = ? AND ";\n    $bindValues[] = $val;\n}\n\n$sql = "SELECT * FROM t1 WHERE " . substr($where, 0, strlen($where) - 4);\n$stmt->prepare($sql);\n\n$stmt->bind_param($types, ...$bindValues);