$ch = curl_init('https://api.twitter.com/1.1/statuses/filter.json?follow=twitterapi');\ncurl_setopt($ch, CURLOPT_RETURNTRANSFER, 1);\ncurl_setopt($ch, CURLOPT_READFUNCTION, 'read_data');\ncurl_setopt($ch, CURLOPT_HEADER, 0);\ncurl_setopt($ch, CURLOPT_HTTPHEADER, array('Authorization: Bearer YOUR_BEARER_TOKEN'));\ncurl_exec($ch);\ncurl_close($ch);\n\nfunction read_data($ch, $string) {\n    // Process the streamed data here\n    echo $string;\n    return strlen($string);\n}