public function send_mail($email='',$subject='',$text='') {\n    $mail = new PHPMailer();\n    $db = new Db();\n    $sql = $db->query("SELECT * FROM configuration WHERE name='shopemail' OR name='shopname'");\n    while($configuration = $db->fetchArray($sql)) {\n        $row[$configuration['name']] = $configuration['value'];\n    }\n    // MIME BOUNDARY\n    $mime_boundary = $data['shopname'].md5(time());\n    $headers = "From: ".$row['shopname']." <".$row['shopemail'].">\n";\n    $headers .= "Reply-To: ".$row['shopname']." <". $row['shopemail'].">\n";\n    $headers .= "MIME-Version: 1.0\n";\n    $headers .= "Content-Type: multipart/alternative; boundary=\"$mime_boundary\"; name=\"$mime_boundary\"\n";\n    $headers .= "Content-ID: <" . $mime_boundary . ">\n";\n    $headers .= "Content-Disposition: inline; filename=\"$mime_boundary.html\"\n";\n    // TEXT EMAIL PART\n    $message = "--$mime_boundary\n";\n    $message .= "Content-Type: text/html; charset=iso-8859-1\n";\n    $message .= "Content-Transfer-Encoding: 8bit\n\n";\n    $message .= $text."\n";\n    // FINAL BOUNDARY\n    $message .= "--$mime_boundary--\n\n";\n    // SEND MAIL\n    $mail_sent = @mail( $email, $subject, $message, $headers );\n}