function roundedCorners($imagePath, $cornerRadius) {\n    $image = imagecreatefromstring(file_get_contents($imagePath));\n    $width = imagesx($image);\n    $height = imagesy($image);\n\n    $mask = imagecreatetruecolor($width, $height);\n    $transparent = imagecolorallocatealpha($mask, 0, 0, 0, 127);\n\n    for ($x = 0; $x < $width; $x++) {\n        for ($y = 0; $y < $height; $y++) {\n            $distance = sqrt(pow($x - $cornerRadius, 2) + pow($y - $cornerRadius, 2));\n            if ($distance < $cornerRadius) {\n                imagefilledrectangle($mask, $x, $y, $x, $y, $transparent);\n            }\n        }\n    }\n\n    $result = imagecreatetruecolor($width, $height);\n    imagecopy($result, $image, 0, 0, 0, 0, $width, $height);\n    imagecopy($result, $mask, 0, 0, 0, 0, $width, $height);\n    imagecolortransparent($result, $transparent);\n\n    header('Content-Type: image/png');\n    imagepng($result);\n    imagedestroy($result);\n}\n\n// Usage:\n$imagePath = 'path/to/image.jpg';\n$cornerRadius = 20;\nroundedCorners($imagePath, $cornerRadius);