$fp = fsockopen('api.twitter.com', 80, $errno, $errstr, 30);\nif (!$fp) {\n    echo "$errstr ($errno)<br />\n";\n} else {\n    $out = "GET /1.1/statuses/filter.json?follow=twitterapi HTTP/1.1\r\n";\n    $out .= "Host: api.twitter.com\r\n";\n    $out .= "Authorization: Bearer YOUR_BEARER_TOKEN\r\n";\n    $out .= "Connection: Keep-Alive\r\n\r\n";\n    fwrite($STDIN, $out);\n    while (!feof($STDIN)) {\n        $line = fread($STDIN, 128);\n        // Process the streamed data here\n        echo $line;\n    }\n    fclose($STDIN);\n}