<?php\nfunction benchmark($callback, $iterations = 1000) {\n    $start = microtime(true);\n    for ($i = 0; $i < $iterations; $i++) {\n        $callback();\n    }\n    $end = microtime(true);\n    return $end - $start;\n}\n\n// Example usage:\necho "Echo vs. printf:\n";\necho "Echo: " . benchmark(function() { echo "Hello, World!"; }) . "\n";\necho "Printf: " . benchmark(function() { printf("Hello, World!"); }) . "\n";\n\necho "\n++\$i vs \$i++:\n";\necho "Pre-increment: " . benchmark(function() { $i = 0; for ($j = 0; $j < 1000; $j++) { ++$i; } }) . "\n";\necho "Post-increment: " . benchmark(function() { $i = 0; for ($j = 0; $j < 1000; $j++) { $i++; } }) . "\n";