$mysqli = new mysqli("localhost", "username", "password", "database");\n\n$query = "SELECT SQL_CALC_FOUND_ROWS Id, Name FROM my_table WHERE Name LIKE '%prashant%' LIMIT 0, 10;";\n$query .= "SELECT FOUND_ROWS();";\n\nif ($mysqli->multi_query($query)) {\n    do {\n        if ($result = $mysqli->store_result()) {\n            while ($row = $result->fetch_row()) {\n                // Handle each table set separately\n                // For the first query, $row will contain Id and Name\n                // For the second query, $row will contain the count\n                print_r($row);\n            }\n            $result->free();\n        }\n    } while ($mysqli->next_result());\n}