$pdf = new Zend_Pdf();\n\n// Create a new page\n$page = $pdf->newPage(Zend_Pdf_Page::SIZE_A4);\n\n// Draw a shape (e.g. a rectangle)\n$page->setFillColor(Zend_Pdf_Color_Html::color('red'));\n$page->drawRectangle(100, 100, 200, 200);\n\n// Save the current graphics state\n$page->saveGS();\n\n// Draw the text on top of the shape\n$page->setFont(Zend_Pdf_Font::fontWithName('Helvetica'), 24);\n$page->drawText('Hello, World!', 150, 150);\n\n// Restore the previous graphics state\n$page->restoreGS();\n\n// Add the page to the PDF\n$pdf->pages[] = $page;\n\n// Save the PDF to a file\n$pdf->save('example.pdf');