function checkUrl($url, $timeout = 5) {\n    $ch = curl_init($url);\n    curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);\n    curl_setopt($ch, CURLOPT_TIMEOUT, $timeout);\n    $response = curl_exec($ch);\n    $httpcode = curl_getinfo($ch, CURLINFO_HTTP_CODE);\n    curl_close($ch);\n    \n    if ($httpcode == 200) {\n        return true;\n    } else {\n        return false;\n    }\n}\n\n$urls = array('http://example.com', 'http://broken-url.com', ...);\n\nforeach ($urls as $url) {\n    if (!checkUrl($url)) {\n        // flag URL as potentially broken\n    }\n}