$data = array(\n    array('title' => 'Entry 1', 'description' => 'entry 1 description'),\n    array('title' => 'Entry 2', 'description' => 'entry 2 description'),\n    array('title' => 'Entry 3', 'description' => 'entry 3 description'),\n    array('title' => 'Entry 4', 'description' => 'entry 4 description'),\n    array('title' => 'Entry 5', 'description' => 'entry 5 description')\n);\n\n$columns = 2;\n$rows = ceil(count($data) / $columns);\n\nfor ($i = 0; $i < $rows; $i++) {\n    echo '<div class="row">';\n    for ($j = 0; $j < $columns; $j++) {\n        $index = $i * $columns + $j;\n        if (isset($data[$index])) {\n            echo '<div class="column">';\n            echo '<h2>' . $data[$index]['title'] . '</h2>';\n            echo '<p>' . $data[$index]['description'] . '</p>';\n            echo '</div>';\n        } else {\n            echo '<div class="column">&nbsp;</div>';\n        }\n    }\n    echo '</div>';\n}